-- ----------------------------
-- View structure for ranks
-- ----------------------------
DROP VIEW IF EXISTS `ranks`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `ranks` AS select `score`.`stuNumber` AS `stuNumber`,`score`.`name` AS `name`,`score`.`couName` AS `couName`,`score`.`grade` AS `grade`,`score`.`point` AS `point`,rank() OVER (PARTITION BY `score`.`couName` ORDER BY `score`.`grade` desc )  AS `ranks` from `score`;

-- ----------------------------
-- View structure for score
-- ----------------------------
DROP VIEW IF EXISTS `score`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `score` AS select `st_grade`.`stuNumber` AS `stuNumber`,`st_grade`.`couId` AS `couId`,`st_name`.`name` AS `name`,`st_course`.`couName` AS `couName`,`st_grade`.`grade` AS `grade`,`st_grade`.`point` AS `point`,`st_course`.`major` AS `major` from ((`st_course` join `st_name`) join `st_grade`) where ((`st_grade`.`stuNumber` = `st_name`.`stuNumber`) and (`st_grade`.`couId` = `st_course`.`couId`));

